pageContext.rootQuery:
query PageQuery($uid: String) {
  prismic {
    allArticles(uid: $uid) {
      edges {
        node {
          title
          date_published
          _linkType
          _meta {
            lastPublicationDate
            firstPublicationDate
          }
          body1 {
            ... on PRISMIC_ArticleBody1Body_section {
              type
              label
              primary {
                body_section
              }
            }
            ... on PRISMIC_ArticleBody1Code_block {
              type
              label
              primary {
                code_block
              }
            }
          }
        }
      }
    }
  }
}
